    <span style="color:#ff79c6">return</span> [{title<span style="color:#ff79c6">:</span><span style="color:#f1fa8c">&#39;标题一&#39;</span>, author<span style="color:#ff79c6">:</span> <span style="color:#f1fa8c">&#39;作者一&#39;</span>},{title<span style="color:#ff79c6">:</span><span style="color:#f1fa8c">&#39;标题二&#39;</span>, author<span style="color:#ff79c6">:</span> <span style="color:#f1fa8c">&#39;作者二&#39;</span>},{title<span style="color:#ff79c6">:</span><span style="color:#f1fa8c">&#39;标题三&#39;</span>, author<span style="color:#ff79c6">:</span> <span style="color:#f1fa8c">&#39;作者三&#39;</span>},{title<span style="color:#ff79c6">:</span><span style="color:#f1fa8c">&#39;标题四&#39;</span>, author<span style="color:#ff79c6">:</span> <span style="color:#f1fa8c">&#39;作者四&#39;</span>},{title<span style="color:#ff79c6">:</span><span style="color:#f1fa8c">&#39;标题五&#39;</span>, author<span style="color:#ff79c6">:</span> <span style="color:#f1fa8c">&#39;作者五&#39;</span>}]
  }
