        <span style="color:#ff79c6">if</span> (item.title.indexOf(searchVal) <span style="color:#ff79c6">&gt;=</span> <span style="color:#bd93f9">0</span>) {
          <span style="color:#ff79c6">return</span> item
        } 
      })         
